# ── Defaults (override on the command line) ───────────────────────────────────
PROVIDER     ?= anthropic
MODEL        ?= claude-opus-4-6
URL          ?=
JUPYTER_PORT ?= 8888
REGISTRY     ?= docker.io/youruser
K8S_NS       ?= mlops

# Prepend .venv/bin when it exists (created on macOS by make install).
# All targets that invoke Python tools pick up venv binaries automatically.
VENV_BIN := $(shell [ -d .venv ] && echo "$(PWD)/.venv/bin:" || echo "")
export PATH := $(VENV_BIN)$(PATH)

.PHONY: help install start stop cli \
        k8s-build k8s-push k8s-secret k8s-deploy k8s-delete k8s-forward k8s-kserve \
        pkg-test

.DEFAULT_GOAL := help

# ── help ──────────────────────────────────────────────────────────────────────
help:
	@echo ""
	@echo "Usage:"
	@echo "  make install                       Set up Python, install deps, configure API keys"
	@echo "  make start                         Start Jupyter and MLflow, open in browser"
	@echo "  make stop                          Stop Jupyter and MLflow"
	@echo "  make cli URL=https://...           Generate a Python CLI from a docs URL"
	@echo ""
	@echo "Kubernetes targets (override REGISTRY and K8S_NS as needed):"
	@echo "  make k8s-build                     Build Docker images for jupyter and mlflow"
	@echo "  make k8s-push                      Push images to \$$REGISTRY (default: $(REGISTRY))"
	@echo "  make k8s-secret                    Create mlops-secrets from .env (idempotent)"
	@echo "  make k8s-deploy                    Apply all k8s manifests"
	@echo "  make k8s-delete                    Delete all k8s manifests"
	@echo "  make k8s-forward                   Port-forward jupyter:8888 and mlflow:5000 to localhost"
	@echo "  make k8s-kserve                    Install KServe in Knative serverless mode"
	@echo ""
	@echo "Parameters for make cli (with defaults):"
	@echo "  PROVIDER=$(PROVIDER)   anthropic | huggingface"
	@echo "  MODEL=$(MODEL)"
	@echo "  URL=$(URL)"
	@echo "  JUPYTER_PORT=$(JUPYTER_PORT)"
	@echo ""
	@echo "Monorepo package targets:"
	@echo "  make pkg-test NAME=mypackage           Run tests for one package"
	@echo "  make pkg-test                          Run tests for all packages"
	@echo ""
	@echo "Examples:"
	@echo "  make cli URL=https://docs.anthropic.com/en/api/messages"
	@echo "  make cli URL=https://huggingface.co/docs/hub/api PROVIDER=hf MODEL=mistralai/Mistral-7B-Instruct-v0.3"
	@echo "  make k8s-build k8s-push REGISTRY=docker.io/myuser"
	@echo ""

# ── install ───────────────────────────────────────────────────────────────────
## Check Python, create venv, install dependencies + Jupyter, create notebooks/,
## and prompt for HuggingFace / Anthropic API keys (skipped when .env exists).
install:
	@chmod +x scripts/setup_jupyter.sh scripts/setup_mlflow.sh scripts/setup_vllm.sh
	@bash scripts/setup_jupyter.sh
	@bash scripts/setup_mlflow.sh
	@bash scripts/setup_vllm.sh
	@echo ""
	@echo "All done. Run 'make start' to launch Jupyter and MLflow."

# ── start ─────────────────────────────────────────────────────────────────────
## Launch Jupyter and MLflow in the background and open them in your browser.
start:
	@[ -f .env ] || { echo "Run 'make install' first to install dependencies and configure API keys."; exit 1; }
	@mkdir -p notebooks
	@set -a && . ./.env && set +a && \
	  nohup jupyter notebook --port $(JUPYTER_PORT) --notebook-dir notebooks > .jupyter.log 2>&1 & echo $$! > .jupyter.pid
	@echo "Jupyter  : starting on port $(JUPYTER_PORT) … (log: .jupyter.log)"
	@set -a && . ./.env && set +a && \
	  nohup mlflow server --host 127.0.0.1 --port 5000 > .mlflow.log 2>&1 & echo $$! > .mlflow.pid
	@echo "MLflow   : starting at http://127.0.0.1:5000 … (log: .mlflow.log)"
	@sleep 2
	@open http://localhost:$(JUPYTER_PORT) 2>/dev/null || \
	  xdg-open http://localhost:$(JUPYTER_PORT) 2>/dev/null || \
	  echo "Open http://localhost:$(JUPYTER_PORT) in your browser."
	@open http://127.0.0.1:5000 2>/dev/null || \
	  xdg-open http://127.0.0.1:5000 2>/dev/null || true

# ── stop ──────────────────────────────────────────────────────────────────────
## Stop Jupyter and MLflow servers.
stop:
	@PID=""; \
	[ -f .jupyter.pid ] && PID=$$(cat .jupyter.pid); \
	if [ -n "$$PID" ] && kill "$$PID" 2>/dev/null; then \
	  echo "Jupyter  : stopped (PID $$PID)."; rm -f .jupyter.pid; \
	elif pkill -f "jupyter.*notebook" 2>/dev/null; then \
	  echo "Jupyter  : process killed."; rm -f .jupyter.pid; \
	else \
	  echo "Jupyter  : no server found."; \
	fi
	@PID=""; \
	[ -f .mlflow.pid ] && PID=$$(cat .mlflow.pid); \
	if [ -n "$$PID" ] && kill "$$PID" 2>/dev/null; then \
	  echo "MLflow   : stopped (PID $$PID)."; rm -f .mlflow.pid; \
	elif pkill -f "mlflow.*server" 2>/dev/null; then \
	  echo "MLflow   : process killed."; rm -f .mlflow.pid; \
	else \
	  echo "MLflow   : no server found."; \
	fi

# ── cli ───────────────────────────────────────────────────────────────────────
## Generate a Python CLI package from a Python SDK documentation URL.
## Usage: make cli URL=https://... [PROVIDER=anthropic|huggingface] [MODEL=model-name]
## Output: cli/<pkg>/ — a fully scaffolded Poetry package saved in the repo.
cli:
	@[ -f .env ] || { echo "Run 'make install' first to install dependencies and configure API keys."; exit 1; }
ifndef URL
	$(error URL is required. Usage: make cli URL=https://... [PROVIDER=anthropic|huggingface] [MODEL=model-name])
endif
	@echo ""
	@echo "Provider : $(PROVIDER)"
	@echo "Model    : $(MODEL)"
	@echo "URL      : $(URL)"
	@echo ""
	@set -a && . ./.env && set +a && \
	  PROVIDER=$(PROVIDER) MODEL=$(MODEL) URL=$(URL) \
	  papermill notebooks/0_generate_cli.ipynb \
	    /tmp/cli_out_$$(date +%s).ipynb \
	    -p PROVIDER "$(PROVIDER)" \
	    -p MODEL    "$(MODEL)" \
	    -p URL      "$(URL)"

# ── k8s-build ─────────────────────────────────────────────────────────────────
## Build Docker images for jupyter and mlflow.
k8s-build:
	docker build -t $(REGISTRY)/jupyter:latest -f docker/jupyter/Dockerfile .
	docker build -t $(REGISTRY)/mlflow:latest  -f docker/mlflow/Dockerfile  .

# ── k8s-push ──────────────────────────────────────────────────────────────────
## Push images to the container registry.
## Usage: make k8s-push REGISTRY=docker.io/myuser
k8s-push:
	docker push $(REGISTRY)/jupyter:latest
	docker push $(REGISTRY)/mlflow:latest

# ── k8s-secret ────────────────────────────────────────────────────────────────
## Create mlops-secrets in the cluster from the local .env file (idempotent).
k8s-secret:
	@[ -f .env ] || { echo "No .env found. Run 'make install' first."; exit 1; }
	@HF_TOKEN=$$(grep -E '^HF_TOKEN=' .env | cut -d= -f2-) && \
	 ANTHROPIC_API_KEY=$$(grep -E '^ANTHROPIC_API_KEY=' .env | cut -d= -f2-) && \
	 kubectl create secret generic mlops-secrets \
	   --namespace=$(K8S_NS) \
	   --from-literal=HF_TOKEN="$$HF_TOKEN" \
	   --from-literal=ANTHROPIC_API_KEY="$$ANTHROPIC_API_KEY" \
	   --dry-run=client -o yaml | kubectl apply -f -
	@echo "Secret mlops-secrets applied in namespace $(K8S_NS)."

# ── k8s-deploy ────────────────────────────────────────────────────────────────
## Apply all Kubernetes manifests (namespace → configmap → pvcs → deployments → services).
## Substitute REGISTRY into deployment manifests before applying.
k8s-deploy:
	kubectl apply -f k8s/namespace.yaml
	kubectl apply -f k8s/configmap.yaml
	kubectl apply -f k8s/prompts-configmap.yaml
	kubectl apply -f k8s/jupyter/pvc.yaml
	kubectl apply -f k8s/mlflow/pvc.yaml
	REGISTRY=$(REGISTRY) envsubst < k8s/jupyter/deployment.yaml | kubectl apply -f -
	REGISTRY=$(REGISTRY) envsubst < k8s/mlflow/deployment.yaml  | kubectl apply -f -
	kubectl apply -f k8s/jupyter/service.yaml
	kubectl apply -f k8s/mlflow/service.yaml
	@echo "All manifests applied. Check status with: kubectl get pods -n $(K8S_NS)"

# ── k8s-delete ────────────────────────────────────────────────────────────────
## Delete all Kubernetes resources in the mlops namespace.
k8s-delete:
	kubectl delete -f k8s/jupyter/service.yaml    --ignore-not-found
	kubectl delete -f k8s/mlflow/service.yaml     --ignore-not-found
	REGISTRY=$(REGISTRY) envsubst < k8s/jupyter/deployment.yaml | kubectl delete -f - --ignore-not-found
	REGISTRY=$(REGISTRY) envsubst < k8s/mlflow/deployment.yaml  | kubectl delete -f - --ignore-not-found
	kubectl delete -f k8s/jupyter/pvc.yaml        --ignore-not-found
	kubectl delete -f k8s/mlflow/pvc.yaml         --ignore-not-found
	kubectl delete -f k8s/configmap.yaml          --ignore-not-found
	kubectl delete -f k8s/namespace.yaml          --ignore-not-found

# ── k8s-forward ───────────────────────────────────────────────────────────────
## Port-forward jupyter and mlflow to localhost (useful for minikube or headless clusters).
## Press Ctrl-C to stop.
k8s-forward:
	@echo "Forwarding jupyter → http://localhost:8888"
	@echo "Forwarding mlflow  → http://localhost:5000"
	@echo "Press Ctrl-C to stop."
	@kubectl port-forward -n $(K8S_NS) svc/jupyter 8888:8888 &
	@kubectl port-forward -n $(K8S_NS) svc/mlflow  5000:5000 &
	@wait

# ── k8s-kserve ────────────────────────────────────────────────────────────────
## Install KServe in Knative serverless mode.
## Installs: Knative Serving v1.19, Istio 1.28, Cert Manager v1.15, KServe v0.16.
## Requires a running Kubernetes 1.32+ cluster with kubectl configured.
## Uses Helm when available, falls back to YAML.
k8s-kserve:
	@chmod +x scripts/setup_kserve_knative.sh
	@bash scripts/setup_kserve_knative.sh

# ── pkg-test ──────────────────────────────────────────────────────────────────
## Run tests for one package or all packages.
## Usage: make pkg-test NAME=mypackage   (single)
##        make pkg-test                  (all)
pkg-test:
ifdef NAME
	@cd "packages/$(NAME)" && poetry install --quiet && poetry run pytest --tb=short -q
else
	@FAILED=""; \
	for dir in packages/*/ cli/*/; do \
	  [ -f "$$dir/pyproject.toml" ] || continue; \
	  echo "── Testing: $$dir"; \
	  (cd "$$dir" && poetry install --quiet && poetry run pytest --tb=short -q) \
	    || FAILED="$$FAILED $$dir"; \
	done; \
	[ -z "$$FAILED" ] && echo "All packages passed." || { echo "FAILED:$$FAILED"; exit 1; }
endif
